function KeyBoard() {
    const Inp = React.useRef(null)
    const {SheepCounter} = useSheepsContext()
    const {setSheepCounter} = useSheepsContext()

    console.log(SheepCounter)
    const KeyBoard = [
        [1,2,3],
        [4,5,6],
        [7,8,9]
    ]

    return (
    <div className='keyboard'>
            <inpute  ref={Inp} style={{display: 'hidden'}}/>

        {
            KeyBoard.map( (row, key) => (
                <div className='row-container' key={key}>
               {
                    row.map( (key, index) =>(
                            <div className='keyboard--key' key={`${index}-${key}`}>{key}</div>                    
                    ))
                    }
             </div>
            ) )
                }
            <div className='keyboard--submit' 
            onClick={()=>{
                setSheepCounter(prevValue => prevValue + 1 )
            }}
            >Go</div>
    </div>
  )
}

export default KeyBoard
