import React from "react";
import { useSheepsContext } from "../provider/Counter";



function KeyBoard() {
  const Inp = React.useRef(null);
  const { SheepCounter } = useSheepsContext();
  const { setSheepCounter } = useSheepsContext();

  function SetInputValue(event) {
    const clickedNumber = event.target.innerText;
    Inp.current.value +=clickedNumber;
    console.log(Inp.current.value);
}


  const KeyBoard = [
    [1, 2, 3],
    [4, 5, 6],
    [7, 8, 9],
  ];

  return (
    <div className="keyboard">
      <input ref={Inp} style={{ display: "none" }} />

      {KeyBoard.map((row, key) => (
        <div className="row-container" key={key}>
          {row.map((key, index) => (
            <div className="keyboard--key" key={`${index}-${key}`}
                onClick={SetInputValue}
            >
              {key}
            </div>
          ))}
        </div>
      ))}
      <div className="keyboard--submit">Go</div>
    </div>
  );
}

export default KeyBoard;
